INSERT INTO `post_advise` VALUES ('65fb113d-6d92-4c02-8e4d-3d2aa96bbbe3','56bd646a-44cf-4ae2-960c-d7fc24c528e5','Tổ chức ngày hội thể thao','Sắp tới là ngày kỷ niệm, các anh chị cựu sinh viên có muốn mình tổ chức giao lưu môn thể thao nào không ạ?','2024-07-07 15:44:31','2024-07-07 15:44:31','2024-07-07 15:44:31',2,2,3,1,1),('756b3209-458c-40c7-bc71-0d48db772d02','56bd646a-44cf-4ae2-960c-d7fc24c528f2','Hỏi về việc mua xe','Trong đây có ai đã đi chiếc ford này có thể cho em chút lời khuyên được không ạ','2024-07-07 15:56:12','2024-07-07 15:56:12','2024-07-07 15:56:12',2,1,3,0,0),('d9e79351-1b3b-4651-849b-08a75eb6895b','56bd646a-44cf-4ae2-960c-d7fc24c528f4','Hỏi về công ty','Em chào các anh chị, các anh chị đã và đang làm việc tại FPT cho em lời khuyên với , em có nên làm việc tại đó và cũng như là môi trường như nào ạ.','2024-07-07 15:26:27','2024-07-07 15:38:38','2024-07-07 15:26:27',2,3,3,0,0);
/*!40000 ALTER TABLE `post_advise` ENABLE KEYS */;
